<?phprequire './cxmail.php';require './class.smtp.php';header("Access-Control-Allow-Origin: *");header("Access-Control-Allow-Methods: PUT, GET, POST");header("Access-Control-Allow-Headers: Origin, X-Requested-With, Content-Type, Accept");function stripHtmlTags( $text ){    $text = preg_replace(    array(            // Remove invisible content           '@<head[^>]*?>.*?</head>@siu',           '@<style[^>]*?>.*?</style>@siu',           '@<script[^>]*?.*?</script>@siu',           '@<object[^>]*?.*?</object>@siu',           '@<embed[^>]*?.*?</embed>@siu',           '@<applet[^>]*?.*?</applet>@siu',           '@<noframes[^>]*?.*?</noframes>@siu',           '@<noscript[^>]*?.*?</noscript>@siu',           '@<noembed[^>]*?.*?</noembed>@siu',            // Add line breaks before and after blocks           '@</?((address)|(blockquote)|(center)|(del))@iu',           '@</?((div)|(h[1-9])|(ins)|(isindex)|(p)|(pre))@iu',           '@</?((dir)|(dl)|(dt)|(dd)|(li)|(menu)|(ol)|(ul))@iu',           '@</?((table)|(th)|(td)|(caption))@iu',           '@</?((form)|(button)|(fieldset)|(legend)|(input))@iu',           '@</?((label)|(select)|(optgroup)|(option)|(textarea))@iu',           '@</?((frameset)|(frame)|(iframe))@iu',    ), array(' ', ' ', ' ', ' ', ' ', ' ', ' ', ' ', ' ',"\n\$0", "\n\$0", "\n\$0", "\n\$0", "\n\$0", "\n\$0","\n\$0", "\n\$0",),$text );   return strip_tags( $text );}	function makeSafeRequest($variable) {    $variable = stripHtmlTags($variable);    $bad = array("=","<", ">", "/","\"","`","~","'","$","%","#");    $variable = str_replace($bad, "", $variable);    //$variable = mysql_real_escape_string(trim($variable));    return $variable;}if (!defined("PHP_EOL")) define("PHP_EOL", "\r\n");// Email address verification, do not edit.$fullname = makeSafeRequest($_POST['fullName']);$companyName = makeSafeRequest($_POST['companyName']);$phoneNumber = makeSafeRequest($_POST['phoneNumber']);$eMail = makeSafeRequest($_POST['eMail']);$message = makeSafeRequest($_POST['message']);$e_content = "\"Full Name: $fullname\"" . PHP_EOL . PHP_EOL;$e_content .= "\"Company Name: $companyName\"" . PHP_EOL . PHP_EOL;$e_content .= "\"Email: $eMail\"" . PHP_EOL . PHP_EOL;$e_content .= "\"Phone Number: $phoneNumber\"" . PHP_EOL . PHP_EOL;$e_content .= "\"Message: $message\"" . PHP_EOL . PHP_EOL;$e_reply = "You can contact $fullname via email, $eMail";$msg = wordwrap($e_content . $e_reply, 70 );$mail = new cxmail;//$mail->SMTPDebug = 3;                               // Enable verbose debug output$mail->Host = 'smtp.phenologix.com';  // Specify main and backup SMTP servers$mail->SMTPAuth = true;                               // Enable SMTP authentication$mail->Username = 'email here';                 // SMTP username$mail->Password = 'password here';                           // SMTP password$mail->SMTPSecure = 'tls';                            // Enable TLS encryption, `ssl` also accepted$mail->Port = 25;                                    // TCP port to connect to//$mail->addCustomHeader('Message-ID','325325234523452354@imranamanat.com');$mail->setFrom('info@phenologix.com', 'Phenologix');$mail->Subject = "You have been contacted by ".$fullname." - ".$companyName;$mail->Body    = $msg;if(!$mail->send()) {        echo 'error';} else {		echo "200"                $mailx = new cxmail;        $mailx->Host = 'smtp.phenologix.com'; $mailx->SMTPAuth = true; $mailx->Username = 'email here'; $mailx->Password = 'password here'; $mailx->SMTPSecure = 'tls'; $mailx->Port = 25; $mailx->setFrom('email here', 'Phenologix');           $mailx->addAddress($eMail,$fullname);        $mailx->Subject = "Thank you for your interest.";        $mailx->Body = "Dear Sir/Madam, " . PHP_EOL . PHP_EOL . "Thank you for reaching out to us. Our team would contact you soon." . PHP_EOL . PHP_EOL . "Phenologix Team";        $mailx->send();}