<?phprequire './cxmail.php';require './class.smtp.php';header("Access-Control-Allow-Origin: *");header("Access-Control-Allow-Methods: PUT, GET, POST");header("Access-Control-Allow-Headers: Origin, X-Requested-With, Content-Type, Accept");function stripHtmlTags( $text ){    $text = preg_replace(    array(            // Remove invisible content           '@<head[^>]*?>.*?</head>@siu',           '@<style[^>]*?>.*?</style>@siu',           '@<script[^>]*?.*?</script>@siu',           '@<object[^>]*?.*?</object>@siu',           '@<embed[^>]*?.*?</embed>@siu',           '@<applet[^>]*?.*?</applet>@siu',           '@<noframes[^>]*?.*?</noframes>@siu',           '@<noscript[^>]*?.*?</noscript>@siu',           '@<noembed[^>]*?.*?</noembed>@siu',            // Add line breaks before and after blocks           '@</?((address)|(blockquote)|(center)|(del))@iu',           '@</?((div)|(h[1-9])|(ins)|(isindex)|(p)|(pre))@iu',           '@</?((dir)|(dl)|(dt)|(dd)|(li)|(menu)|(ol)|(ul))@iu',           '@</?((table)|(th)|(td)|(caption))@iu',           '@</?((form)|(button)|(fieldset)|(legend)|(input))@iu',           '@</?((label)|(select)|(optgroup)|(option)|(textarea))@iu',           '@</?((frameset)|(frame)|(iframe))@iu',    ), array(' ', ' ', ' ', ' ', ' ', ' ', ' ', ' ', ' ',"\n\$0", "\n\$0", "\n\$0", "\n\$0", "\n\$0", "\n\$0","\n\$0", "\n\$0",),$text );   return strip_tags( $text );}	function jalsonicMakeSafeRequest($variable) {    $variable = stripHtmlTags($variable);    $bad = array("=","<", ">", "/","\"","`","~","'","$","%","#");    $variable = str_replace($bad, "", $variable);    //$variable = mysql_real_escape_string(trim($variable));    return $variable;}if (!defined("PHP_EOL")) define("PHP_EOL", "\r\n");// Email address verification, do not edit.function isEmail($email) {	return(preg_match("/^[-_.[:alnum:]]+@((([[:alnum:]]|[[:alnum:]][[:alnum:]-]*[[:alnum:]])\.)+(ad|ae|aero|af|ag|ai|al|am|an|ao|aq|ar|arpa|as|at|au|aw|az|ba|bb|bd|be|bf|bg|bh|bi|biz|bj|bm|bn|bo|br|bs|bt|bv|bw|by|bz|ca|cc|cd|cf|cg|ch|ci|ck|cl|cm|cn|co|com|coop|cr|cs|cu|cv|cx|cy|cz|de|dj|dk|dm|do|dz|ec|edu|ee|eg|eh|er|es|et|eu|fi|fj|fk|fm|fo|fr|ga|gb|gd|ge|gf|gh|gi|gl|gm|gn|gov|gp|gq|gr|gs|gt|gu|gw|gy|hk|hm|hn|hr|ht|hu|id|ie|il|in|info|int|io|iq|ir|is|it|jm|jo|jp|ke|kg|kh|ki|km|kn|kp|kr|kw|ky|kz|la|lb|lc|li|lk|lr|ls|lt|lu|lv|ly|ma|mc|md|mg|mh|mil|mk|ml|mm|mn|mo|mp|mq|mr|ms|mt|mu|museum|mv|mw|mx|my|mz|na|name|nc|ne|net|nf|ng|ni|nl|no|np|nr|nt|nu|nz|om|org|pa|pe|pf|pg|ph|pk|pl|pm|pn|pr|pro|ps|pt|pw|py|qa|re|ro|ru|rw|sa|sb|sc|sd|se|sg|sh|si|sj|sk|sl|sm|sn|so|sr|st|su|sv|sy|sz|tc|td|tf|tg|th|tj|tk|tm|tn|to|tp|tr|tt|tv|tw|tz|ua|ug|uk|um|us|uy|uz|va|vc|ve|vg|vi|vn|vu|wf|ws|ye|yt|yu|za|zm|zw)$|(([0-9][0-9]?|[0-1][0-9][0-9]|[2][0-4][0-9]|[2][5][0-5])\.){3}([0-9][0-9]?|[0-1][0-9][0-9]|[2][0-4][0-9]|[2][5][0-5]))$/i",$email));}$fullname = jalsonicMakeSafeRequest($_POST['fullName']);$companyName = jalsonicMakeSafeRequest($_POST['companyName']);$phoneNumber = jalsonicMakeSafeRequest($_POST['phoneNumber']);$eMail = jalsonicMakeSafeRequest($_POST['eMail']);$message = jalsonicMakeSafeRequest($_POST['message']);if(trim($fullname) == '') {	echo '<div class="error_message">You must enter your name.</div>';	exit();} else if(trim($eMail) == '') {	echo '<div class="error_message">Please enter a valid email address.</div>';	exit();} else if(!isEmail($eMail)) {	echo '<div class="error_message">You have entered an invalid e-mail address. Please try again.</div>';	exit();}if(trim($message) == '') {	echo '<div class="error_message">Please enter your message.</div>';	exit();}$e_content = "\"Full Name: $fullname\"" . PHP_EOL . PHP_EOL;$e_content .= "\"Company Name: $companyName\"" . PHP_EOL . PHP_EOL;$e_content .= "\"Email: $eMail\"" . PHP_EOL . PHP_EOL;$e_content .= "\"Phone Number: $phoneNumber\"" . PHP_EOL . PHP_EOL;$e_content .= "\"Message: $message\"" . PHP_EOL . PHP_EOL;$e_reply = "You can contact $fullname via email, $eMail";$msg = wordwrap($e_content . $e_reply, 70 );$mail = new cxmail;//$mail->SMTPDebug = 3;                               // Enable verbose debug output$mail->Host = 'smtp.globalsni.com';  // Specify main and backup SMTP servers$mail->SMTPAuth = true;                               // Enable SMTP authentication$mail->Username = 'info@globalsni.com';                 // SMTP username$mail->Password = 'sohaibg007';                           // SMTP password$mail->SMTPSecure = 'tls';                            // Enable TLS encryption, `ssl` also accepted$mail->Port = 25;                                    // TCP port to connect to//$mail->addCustomHeader('Message-ID','325325234523452354@digidoc.com.pk');$mail->setFrom('info@globalsni.com', 'Global SNI');$mail->Subject = "You have been contacted by ".$fullname." - ".$companyName;$mail->Body    = $msg;if(!$mail->send()) {        echo '<div class="error_message">Ooops. Sending Error</div>';} else {    	echo "<fieldset>";	echo "<div id='success_page'>";	echo "<h3>Email Sent Successfully.</h3>";	echo "<p>Thank you <strong>$fullname</strong>, your message has been submitted to us.</p>";	echo "</div>";                $mailx = new cxmail;        $mailx->Host = 'smtp.globalsni.com'; $mailx->SMTPAuth = true; $mailx->Username = 'support@digidoc.com.pk'; $mailx->Password = 'sohaibg007'; $mailx->SMTPSecure = 'tls'; $mailx->Port = 25; $mailx->setFrom('support@digidoc.com.pk', 'DigiDoc');           $mailx->addAddress($eMail,$fullname);        $mailx->Subject = "Thank you for your interest.";        $mailx->Body = "Dear Sir/Madam, " . PHP_EOL . PHP_EOL . "Thank you for reaching out to us. Our team would contact you soon." . PHP_EOL . PHP_EOL . "GlobalSNI Team";        $mailx->send();        }//	echo "<fieldset>";////	echo "<div id='success_page'>";////	echo "<h3>Email Sent Successfully.</h3>";////	echo "<p>Thank you <strong>$name</strong>, your message has been su